import m from 'mithril'
import {
  Button,
  Icon,
  Icons,
  Input,
  Checkbox,
  Menu,
  MenuItem,
  Popover,
  PopoverMenu,
  Tag,
  SelectList,
  ListItem,
  Toaster
} from 'construct-ui'

import {
  Nav,
  showToast
} from './Nav'

const mockrequests = [

  {
    nr: '1',
    date: "23/12/20",
    season: "202",
    line: "2",
    model: "m2200162",
    color: "cw425",
    size: "50",
    user: "ntaov",
    client: "elena",
    feedback: "",
    order: ""
  },
  {
    nr: '2',
    date: "23/12/20",
    season: "202",
    line: "1",
    model: "m2300150",
    color: "cs170",
    size: "50",
    user: "mpetrosi",
    client: "natalia",
    feedback: "ok da Parigi, rientra il 10/01",
    order: "#24551 r15"
  },
  {
    nr: '3',
    date: "23/12/20",
    season: "202",
    line: "1",
    model: "m2200295",
    color: "cp015",
    size: "50",
    user: "ntaov",
    client: "kristina",
    feedback: "",
    order: ""
  },
  {
    nr: '4',
    date: "10/06/20",
    season: "202",
    line: "2",
    model: "m2300100",
    color: "cg217",
    size: "50",
    user: "mpetrosi",
    client: "natalia",
    feedback: "ok da Parigi, rientra il 10/01",
    order: "#24551 r15"
  },
  {
    nr: '5',
    date: "21/12/20",
    season: "202",
    line: "2",
    model: "m2200100",
    color: "cw425",
    size: "50",
    user: "ntaov",
    client: "kristina",
    feedback: "",
    order: ""
  },
  {
    nr: '6',
    date: "23/05/20",
    season: "202",
    line: "2",
    model: "m2300100",
    color: "cg217",
    size: "50",
    user: "maki",
    client: "mrochko",
    feedback: "ok da Parigi, rientra il 10/01",
    order: "#24551 r15"
  },
  {
    nr: '7',
    date: "23/12/20",
    season: "202",
    line: "2",
    model: "m2200100",
    color: "cw425",
    size: "50",
    user: "ntaov",
    client: "kristina",
    feedback: "",
    order: ""
  },
  {
    nr: '8',
    date: "23/12/20",
    season: "202",
    line: "2",
    model: "m2300100",
    color: "cg217",
    size: "50",
    user: "mpetrosi",
    client: "natalia",
    feedback: "ok da Parigi, rientra il 10/01",
    order: "#24551 r15"
  }
]

let requests = []
const originalRequests = {}
let changes = {}
const headers = [
  "actions",
  "nr",
  "day",
  "season",
  "line",
  "model",
  "color",
  "size",
  "user",
  "client",
  "feedback",
  "order",
  "status"
];

let filter = {}
// Creating Sets to create unique values to pass to the filter
let mySets = {}

headers.forEach((field, i) => {
  mySets[`${field}Set`] = new Set()
});

function addToSets(req) {
  let vals = Object.values(req)
  let fields = Object.keys(req)

  fields.forEach((field, i) => {
    let val = vals[i]
    if (headers.indexOf(field) != -1) {
      mySets[`${field}Set`].add(val)
    }
  })
}

Object.filter = (obj, filter) => {
  let key, results = []
  let queries = Object.keys(filter).filter(item => filter[item] != null)
  for (key in obj) {
    if (obj.hasOwnProperty(key)) {
      let search = obj[key]
      if (queries.length > 0) {
        for (let i = 0; i < queries.length; i++) {
          let q = queries[i]
          if (!search[q].includes(filter[q])) {
            i++
          } else if (i === queries.length - 1 && search[q].includes(filter[q])) {
            results.push(search)
          }
        }
      } else {
        results = obj
      }
    }
  }
  return results
}

function itemFilter(search, filter) {
  let key, results = false
  let queries = Object.keys(filter).filter(item => filter[item] != null)
  if (queries.length > 0) {
    for (let i = 0; i < queries.length; i++) {
      let q = queries[i]
      if (!search[q].includes(filter[q])) {
        console.log('not a match');
        i++
      } else if (i === queries.length - 1 && search[q].includes(filter[q])) {
        console.log('a match');
        results = true
      }
    }
  } else {
    results = true
  }
  console.log(results);
  return results
}

function clearDiffs() {
  document.querySelectorAll('input.diffed').forEach(item => {
    item.classList.remove('diffed')
  });
  changes = {}
}

function Header(vnode) {

  let header = vnode.attrs.header
  let options = [...mySets[`${header}Set`]]
  let checkedOptions = []
  // const indeterminate = state.checkedOptions.length > 0 && state.checkedOptions < state.options.length
  // const checked = state.checkedOptions.length === state.options.length

  console.log(options);

  let filterCheckboxes = [...mySets[`${header}Set`]].map(item =>
    m(Checkbox, {
      label: item
    })
  )
  console.log(mySets);

  return {
    view(vnode) {

      return m(`.header-tag.header-${header}`, [
        m(Popover, {
          closeOnEscapeKey: true,
          content: m('.checkboxes.flex.column', [
            m(Checkbox, {
              // indeterminate,
              // checked,
              label: 'Select All',
              onchange(e) {
                // state.onCheckAll(e)
              }
            }),
            // filterCheckboxes
          ]),
          trigger: m(Button, {
            label: header,
            fluid: true,
            size: 'xs',
            outlined: true,
            disabled: header === 'actions' ? true : false
            // iconLeft: Icons.FILTER
          })
        }),
        m(Input, {
          placeholder: header,
          class: `header-${header}`,
          disabled: header === 'actions' ? true : false,
          fluid: true,
          outlined: true,
          // Filter text
          oninput: (e) => {
            Richieste.filter[header] = e.target.value.toLowerCase()
          }
        })
      ])
    }
  }
}

let Headers = {
  view(vnode) {
    return headers.map((header) => m(Header, {
      header: header
    }))
  }
};
let MenuComponent = {
  view(vnode) {
